import React, { Component } from "react";
import axios from "axios";
import "./Contact.css";

class Contact extends Component {
    state = {
        firstname: "",
        lastname: "",
        phonenumber: "",
        email: "",
        message: "",
        sent: false
    }

    handleName = (e) => {
        this.setState({
            firstname: e.target.value
        });
    };

    handleLastname = (e) => {
        this.setState({
            lastname: e.target.value
        });
    };

    handlePhonenumber = (e) => {
        this.setState({
            phonenumber: e.target.value
        });
    };

    handleEmail = (e) => {
        this.setState({
            email: e.target.value
        });
    };

    handleMessage = (e) => {
        this.setState({
            message: e.target.value
        });
    };

    formSubmit = (e) => {
        e.preventDefault();
        let data = {
            firstname: this.state.firstname,
            lastname: this.state.lastname,
            phonenumber: this.state.phonenumber,
            email: this.state.email,
            message: this.state.message
        }

        axios.post("/sendemail", data)
        .then(res => {
            this.setState({
                sent: true,
            }, this.resetForm())
        }).catch(() => {
            console.log("Message not sent");
        })
    };

    resetForm = () => {
        this.setState({
            firstname: "",
            lastname: "",
            phonenumber: "",
            email: "",
            message: "",
        })
    }

    render() {
        const { firstname, lastname, phonenumber, email, message } = this.state;
        return (
            <div className="contact">
                <h1>Contact Us</h1>
                <div className="contact-container">
                    <form onSubmit={this.formSubmit}> 
                        <input className="input-box" value={firstname} onChange={this.handleName} placeholder="First Name" type="text" />
                        <input className="input-box" value={lastname} onChange={this.handleLastname} placeholder="Last Name" type="text" />
                        <input className="input-box" value={phonenumber} onChange={this.handlePhonenumber} placeholder="Phone Number" type="tel" />
                        <input className="input-box" value={email} onChange={this.handleEmail} placeholder="Email" type="email" required />
                        <textarea className="message-box" value={message} onChange={this.handleMessage} placeholder="Message" type="text" />
                        <div className={this.state.sent ? "msg msgAppear" : "msg"}>Message has been sent.</div>
                        <button className="submit-btn" type="submit" value="submit">Submit</button>
                    </form>
                    <img className="map" src="images/map.png" alt="" />
                </div>
            </div>
        );
    }
}

export default Contact;
