import React, { Component } from "react";
import "./Contact.css";

class Contact extends Component {
    state = {
        email: {
            firstname: "",
            lastname: "",
            phonenumber: "",
            email: "",
            text: ""
        }
    }

    sendEmail = _ => {
        const { email } = this.state;
        fetch(`/send-email?firstName-${email.firstName}&lastName-${email.lastName}&phoneNumber-${email.phoneNumber}&email-${email.email}&message-${email.message}`)
        .catch(err => console.log(err));
    }

    render() {
        const { email } = this.state;
        return (
            <div className="contact">
                <h1>Contact Us</h1>
                <div className="contact-container">
                    <form>
                        <input className="input-box" value={email.firstName} onChange={e => this.setState({ ...email, firstName: e.target.value } )} placeholder="First Name" type="text" />
                        <input className="input-box" value={email.lastName} onChange={e => this.setState({ ...email, lastName: e.target.value } )} placeholder="Last Name" type="text" />
                        <input className="input-box" value={email.phoneNumber} onChange={e => this.setState({ ...email, phoneNumber: e.target.value } )} placeholder="Phone Number" type="tel" />
                        <input className="input-box" value={email.email} onChange={e => this.setState({ ...email, email: e.target.value } )} placeholder="Email" type="email" />
                        <textarea className="message-box" value={email.text} onChange={e => this.setState({ ...email, message: e.target.value } )} placeholder="Message" type="text" />
                        <button className="submit-btn" type="submit" value="submit">Submit</button>
                    </form>
                    <img className="map" src="images/map.png" alt="" />
                </div>
            </div>
        );
    }
}

export default Contact;
